    <div class="line-item-mobile">Ii</div><div class="line-item-mobile">Jj</div><div class="line-item-mobile">Kk</div><div class="line-item-mobile">Ll</div><div class="line-item-mobile">Mm</div><div class="line-item-mobile">Nn</div><div class="line-item-mobile">Oo</div><div class="line-item-mobile">Pp</div><br/>
    <div class="line-item-mobile">Rr</div><div class="line-item-mobile">Ss</div><div class="line-item-mobile">Tt</div><div class="line-item-mobile">Uu</div><div class="line-item-mobile">Vv</div><div class="line-item-mobile">Yy</div><div class="line-item-mobile">Øø</div><div class="line-item-mobile">Åå</div>
